    public class N
    {
        Random rand = new Random();

        //
        public double Normal()
        {
            double s = 0, u = 0, v = 0;
            while (s > 1 || s == 0)
            {
                u = rand.NextDouble() * 2 - 1;
                v = rand.NextDouble() * 2 - 1;

                s = u * u + v * v;
            }

            var z = Math.Sqrt(-2 * Math.Log(s) / s) * u;
            return (z);
